     6→interface ValidationResult {
     7→  valid: boolean;
     8→  error?: string;
     9→}
    10→
    11→export function validateEmail(email: string): ValidationResult {
    12→  const trimmed = email.trim().toLowerCase();
    13→  if (!trimmed) return { valid: false, error: "Email is required" };
    14→  if (trimmed.length > 254) return { valid: false, error: "Email is too long" };
    15→  if (!EMAIL_REGEX.test(trimmed)) return { valid: false, error: "Invalid email format" };
    16→  return { valid: true };
    17→}
    18→
    19→export function validatePassword(password: string): ValidationResult {
    20→  if (!password) return { valid: false, error: "Password is required" };
    21→  if (password.length < MIN_PASSWORD_LENGTH) {
    22→    return { valid: false, error: `Password must be at least ${MIN_PASSWORD_LENGTH} characters` };
    23→  }
    24→  if (password.length > MAX_PASSWORD_LENGTH) {
    25→    return { valid: false, error: `Password must be at most ${MAX_PASSWORD_LENGTH} characters` };
    26→  }
    27→  if (!/[A-Z]/.test(password)) return { valid: false, error: "Password must contain an uppercase letter" };
    28→  if (!/[a-z]/.test(password)) return { valid: false, error: "Password must contain a lowercase letter" };
    29→  if (!/[0-9]/.test(password)) return { valid: false, error: "Password must contain a digit" };
    30→  return { valid: true };
    31→}
